windowTab.script:

	           jQuery(document).ready(function() {
                    // unit field in products generic tab don't have reference attribute so it's need to use jquery selectors
                    var productUnit = $("#window\\.mainTab\\.product\\.gridLayout\\.unit_input");
                    var costUnit = $("#window\\.costTab\\.gridLayout\\.unitGridLayout\\.unit_input"); 
                    var costNormsComponentList = [
                        #{costTabUnit}, 
                        #{nominalCostCurrency}, 
                        #{lastPurchaseCostCurrency}, 
                        #{averageCostCurrency}
                    ];

                    // this event handler are necessary to update field state after "beforeRender" hook set new value
                    for(var i; i < costNormsComponentList.length; i++) {

                        //temporary, for disable currency fields
                        if(i > 0) {
                            costNormsComponentList[i].setEnabled(false, true);
                        }

                        costNormsComponentList[i].addOnChangeListener({
                            onSetValue: function(value) {
                                costNormsComponentList[i].performUpdateState();
                                costNormsComponentList[i].setEnabled(false, true);
                            }
                        });
                    }

                    productUnit.bind('blur', function() {
                        #{costTabUnit}.setEnabled(true, true);
                        costUnit.attr('value', productUnit.val());
                    });
                });
