smallButton.script:

var pdfMaterialReq = #{window}.getRibbonItem("exportMaterialRequirement.pdfMaterialReq");
var xlsMaterialReq = #{window}.getRibbonItem("exportMaterialRequirement.xlsMaterialReq");
#{grid}.addOnChangeListener({
	onChange: function(selectedEntitiesArray) {
		if (!selectedEntitiesArray || selectedEntitiesArray.length == 0) {
			pdfMaterialReq.disable();
			xlsMaterialReq.disable();
		} else {
			pdfMaterialReq.enable();
			xlsMaterialReq.enable();
		}
	}
});
