component.script:

	var pdfWorker = #{window}.getRibbonItem("exportWorker.pdfWorker");
	var xlsWorker = #{window}.getRibbonItem("exportWorker.xlsWorker");
	var pdfMachine = #{window}.getRibbonItem("exportMachine.pdfMachine");
	var xlsMachine = #{window}.getRibbonItem("exportMachine.xlsMachine");
	var pdfProduct = #{window}.getRibbonItem("exportProduct.pdfProduct");
	var xlsProduct = #{window}.getRibbonItem("exportProduct.xlsProduct");
	var listener = {
		onChange: function(selectedRecord) {
			if (!selectedRecord) {
				pdfWorker.disable();
				xlsWorker.disable();
				pdfMachine.disable();
				xlsMachine.disable();
				pdfProduct.disable();
				xlsProduct.disable();	
			} else if (selectedRecord.fields.generated == '0') {
				pdfWorker.disable("#{translate(products.ribbon.message.recordNotGenerated)}");
				xlsWorker.disable("#{translate(products.ribbon.message.recordNotGenerated)}");
				pdfMachine.disable("#{translate(products.ribbon.message.recordNotGenerated)}");
				xlsMachine.disable("#{translate(products.ribbon.message.recordNotGenerated)}");
				pdfProduct.disable("#{translate(products.ribbon.message.recordNotGenerated)}");
				xlsProduct.disable("#{translate(products.ribbon.message.recordNotGenerated)}");								
			} else {
				pdfWorker.enable();
				xlsWorker.enable();
				pdfMachine.enable();
				xlsMachine.enable();
				pdfProduct.enable();
				xlsProduct.enable();
			}
		}
	}
	this.addOnChangeListener(listener);


	var saveRibbonItem = #{window}.getRibbonItem("actions.save");
	var copyRibbonItem = #{window}.getRibbonItem("actions.copy");
	var generateRibbonItem = #{window}.getRibbonItem("actions.generate");
	var pdfWorker = #{window}.getRibbonItem("exportWorker.pdfWorker");
	var xlsWorker = #{window}.getRibbonItem("exportWorker.xlsWorker");
	var pdfMachine = #{window}.getRibbonItem("exportMachine.pdfMachine");
	var xlsMachine = #{window}.getRibbonItem("exportMachine.xlsMachine");
	var pdfProduct = #{window}.getRibbonItem("exportProduct.pdfProduct");
	var xlsProduct = #{window}.getRibbonItem("exportProduct.xlsProduct");
	
	var containsAtLeastOneOrder = false;
	var entityExists = false;
	
	this.addOnChangeListener({
		onSetValue: function(value) {
			if (!value || !value.content) {
				return;
			}
			if (value.content.entityId) {
				entityExists = true;
			} else {
				entityExists = false;
			}
			updateRibbon();
		}
	});
	#{workPlanComponents}.addOnChangeListener({
		onSetValue: function(value) {
			if (value.content.totalEntities > 0) {
				containsAtLeastOneOrder = true;
			} else {
				containsAtLeastOneOrder = false;
			}
			updateRibbon();
		}
	});
	
	function updateRibbon() {
		if (entityExists) {
			var isGeneratedCheckboxValue = #{generated}.getValue();
			copyRibbonItem.enable();
			if (isGeneratedCheckboxValue && isGeneratedCheckboxValue.content.value == "1") {
				pdfWorker.enable();
				xlsWorker.enable();
				pdfMachine.enable();
				xlsMachine.enable();
				pdfProduct.enable();
				xlsProduct.enable();
				saveRibbonItem.disable("#{translate(products.ribbon.message.recordAlreadyGenerated)}");
				generateRibbonItem.disable("#{translate(products.ribbon.message.recordAlreadyGenerated)}");
			} else {
				pdfWorker.disable("#{translate(products.ribbon.message.recordNotGenerated)}");
				xlsWorker.disable("#{translate(products.ribbon.message.recordNotGenerated)}");
				pdfMachine.disable("#{translate(products.ribbon.message.recordNotGenerated)}");
				xlsMachine.disable("#{translate(products.ribbon.message.recordNotGenerated)}");
				pdfProduct.disable("#{translate(products.ribbon.message.recordNotGenerated)}");
				xlsProduct.disable("#{translate(products.ribbon.message.recordNotGenerated)}");
				saveRibbonItem.enable();
				if (containsAtLeastOneOrder) {
					generateRibbonItem.enable();
				} else {
					generateRibbonItem.disable("#{translate(products.ribbon.message.noOrders)}");
				}
			}
		} else {
			copyRibbonItem.disable("#{translate(recordNotCreated)}");
			pdfWorker.disable("#{translate(recordNotCreated)}");
			xlsWorker.disable("#{translate(recordNotCreated)}");
			pdfMachine.disable("#{translate(recordNotCreated)}");
			xlsMachine.disable("#{translate(recordNotCreated)}");
			pdfProduct.disable("#{translate(recordNotCreated)}");
			xlsProduct.disable("#{translate(recordNotCreated)}");
			saveRibbonItem.enable();
			generateRibbonItem.disable("#{translate(recordNotCreated)}");
		}
	}
